import React, { useState } from "react";
import "./App.css";

function App() {
  const [city, setCity] = useState("");
  const [result, setResult] = useState({
    energy_mix: { solar: 58, wind: 22, battery: 15, grid: 5 },
    co2_reduction: 1.3,
    savings_per_month: 16,
    geji_score: 0.75,
  });
  const [brief, setBrief] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async () => {
    setLoading(true);
    const response = await fetch("http://127.0.0.1:8000/generate-brief", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ city, result }),
    });

    const data = await response.json();
    setBrief(data.brief);
    setLoading(false);
  };

  return (
    <div className="App">
      <h1>ðŸŒ± Greenify My City</h1>
      <input
        type="text"
        value={city}
        onChange={(e) => setCity(e.target.value)}
        placeholder="Enter your city"
      />
      <button onClick={handleSubmit}>Generate Policy Brief</button>
      {loading && <p>Loading...</p>}
      {brief && (
        <div>
          <h2>Generated Policy Brief:</h2>
          <pre style={{ textAlign: "left", whiteSpace: "pre-wrap" }}>{brief}</pre>
        </div>
      )}
    </div>
  );
}

export default App;
