// File: client/src/App.tsx
import React, { useState } from "react";
import "./App.css";
import ReactMarkdown from "react-markdown";
import EnergyChart from "./EnergyChart";
import CityAutocomplete from "./CityAutocomplete";


function App() {
  const [city, setCity] = useState("");
  const [result, setResult] = useState({
    energy_mix: { solar: 58, wind: 22, battery: 15, grid: 5 },
    co2_reduction: 1.3,
    savings_per_month: 16,
    geji_score: 0.75,
  });

  const [brief, setBrief] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async () => {
    setLoading(true);
    try {
      const response = await fetch("http://127.0.0.1:8000/generate-brief", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ city, result }),
      });

      const data = await response.json();
      setBrief(data.brief);
    } catch (error) {
      console.error("Failed to fetch brief:", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <h1>ðŸŒ± Greenify My City</h1>
      <CityAutocomplete onSelect={(selectedCity) => setCity(selectedCity)} />
      <button onClick={handleSubmit}>Generate Policy Brief</button>
      {loading && <p>Loading...</p>}
      {result && <EnergyChart energyMix={result.energy_mix} />}
      {brief && (
        <div className="brief-container">
          <h2>Generated Policy Brief:</h2>
          <ReactMarkdown>{brief}</ReactMarkdown>
        </div>
      )}


    </div>
  );
}

export default App;
